import * as React from 'react';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { Box, FormControl, InputLabel, Select } from '@mui/material';
import { useNavigate } from 'react-router';

export default function BooksButton() {
	const [anchorEl, setAnchorEl] = React.useState(null);
	const [books, setBooks] = React.useState('');
	const bookLinks = ['/A-Womans-Worth'];
	const navigate = useNavigate();
	const open = Boolean(anchorEl);
	const handleClick = (event) => {
		setAnchorEl(event.currentTarget);
	};

	const handleChange = (event) => {
		setBooks(event.target.value);
	};

	const handleClose = () => {
		setAnchorEl(null);
	};

	const handleBookOneNav = ()=> { 
		setAnchorEl(null);
		navigate(bookLinks[0]);
	}
	return (
		<div>
			<Button
				id="basic-button"
				aria-controls={open ? 'basic-menu' : undefined}
				aria-haspopup="true"
				aria-expanded={open ? 'true' : undefined}
				onClick={handleClick}
				sx={{
					my: 2,
					color: '#72320A',
					display: { xs: 'none', md: 'block' },
					fontSize: 15,
					fontFamily: 'inter',
					margin: '1rem',
				}}
			>
				Books
			</Button>
			<Menu
				id="basic-menu"
				anchorEl={anchorEl}
				open={open}
				onClose={handleClose}
				MenuListProps={{
					'aria-labelledby': 'basic-button',
				}}
			>
				<MenuItem onClick={handleBookOneNav}>A Woman's Worth</MenuItem>
			</Menu>

			<div>
				<Box
					sx={{
						minWidth: 100,
						display: { xs: 'block', md: 'none' },
						color: '#72320A',
					}}
				>
					<FormControl fullWidth>
						<InputLabel
							id="demo-simple-select-label"
							sx={{ alignSelf: 'center',alignItems:'center' }}
						>
							Books
						</InputLabel>
						<Select
							labelId="select-label"
							id="select"
							value={books}
							label="Books"
							onChange={handleChange}
						>
							<MenuItem onClick={handleBookOneNav} value={'book1'}>
								A Woman's Worth
							</MenuItem>
						</Select>
					</FormControl>
				</Box>
			</div>
		</div>
	);
}
